import React, { useState } from "react";
import "./FormStyle.css";

const Form = () => {
  const [val, setVal] = useState({
    fName: "",
    eName: "",
    sName: "",
    mName: "",
  });

  const [allEntry, setAllEntry] = useState([]);
  const [fError, setFError] = useState(" ");
  const [eError, setEError] = useState(" ");
  const [pError, setPError] = useState(" ");

  const inputEvent = (e) => {
    const { name, value } = e.target;
    setVal((preVal) => {
      return {
        ...preVal,
        [name]: value,
      };
    });
  };

  const formSubmit = (e) => {
    e.preventDefault();
    const newEntry = {
      fullname: val.fullname,
      email: val.email,
      phone: val.phone,
      msg: val.msg,
    };

    if (newEntry.fullname === "") {
      setFError("*Required this field");
    } else if (newEntry.email === "") {
      setFError("");
      setEError("*Required this field");
    } else if (newEntry.phone === "") {
      setFError("");
      setEError("");
      setPError("*Required this field");
    } else {
      setFError("");
      setEError("");
      setPError("");
      setAllEntry([...allEntry, newEntry]);

      alert(
        `My name is ${val.fullname} My email address is ${val.email} My Phone Number is ${val.phone} and here i want to convey a message that ${val.msg}`
      );
    }
  };
  return (
    <div className="form">
      <form onSubmit={formSubmit}>
        <label>Your Name</label>
        <input
          name="fName"
          value={val.fName}
          onChange={inputEvent}
          type="text"
        />
        <span className='error'>{fError}</span>
        <label>Email</label>
        <input
          name="eName"
          value={val.eName}
          onChange={inputEvent}
          type="email"
        />
        <span className='error'>{eError}</span>
        <label>Subject</label>
        <input
          name="sName"
          value={val.sName}
          onChange={inputEvent}
          type="text"
        />
        <span className='error'>{pError}</span>
        <label>Message</label>
        <textarea
          name="mName"
          value={val.mName}
          onChange={inputEvent}
          rows="6"
          placeholder="Type your message here..."
        />
        <button className="btn">Submit</button>
      </form>
    </div>
  );
};

export default Form;
